import React, { Component } from 'react';
import ItemLister from './ItemLister'
export class ListItems extends Component {
    constructor(props){
        super(props);
        this.state = {
          items:[],

          currentItem:{
            text:'',
            key:''
          }
        }
        this.addItem = this.addItem.bind(this);
        this.handleItem = this.handleItem.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.setUpdate = this.setUpdate.bind(this);
        this.sumList = this.sumList.bind(this);
      }
      addItem(e){
        e.preventDefault();
        const newItem = this.state.currentItem;

        if(newItem.text !==""&& !isNaN(newItem.text)){
          const items = [...this.state.items, newItem];
        this.setState({
          items: items,
          currentItem:{
            text:'',
            key:''
          }
        })
        }
      }

      handleItem (e) {
        this.setState({
            currentItem:{
                text: e.target.value,
                key: Date.now()
             }

        })
      }


      sumList(e){
        e.preventDefault();
        const list = this.state.items;
        var total = 0;
        for(var i = 0, len = list.length; i<len; i++) {
            console.log("total: "+total)
            total += list[i][1];
            console.log("value: "+list[i][1])        }
            console.log("total: "+total)

        /*const items = [...this.state.sumItems, total]
        this.setState({
            sumItems: items
        })*/
      }
      deleteItem(key){
        const filteredItems= this.state.items.filter(item =>
          item.key!==key);
        this.setState({
          items: filteredItems
        })

      }
      setUpdate(text,key){
        const items = this.state.items;
        items.map(item=>{      
          if(item.key===key){
            console.log(item.key +"    "+key)
            item.text= text;
          }
        })
        this.setState({
          items: items
        })


      }
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };
    render(){
        console.log("this.props:"+this.props.values.numPeople+" "+this.props.values.salesTax)
        return (
          <div className="App">
            <h3><center>Enter the price of all the items one at a time for {this.props.values.name}!</center></h3>
            <header>

        <form id="to-do-form" onSubmit={this.addItem}>
          <input type="number" placeholder="Enter task" value= {this.state.currentItem.text} onChange={this.handleItem}></input>
          <button type="submit">Add</button>
        </form>
        <p>{this.state.items.text}</p>

        <ItemLister items={this.state.items} deleteItem={this.deleteItem} setUpdate={this.setUpdate} sumList={this.sumList}/>
        <form id="submitButton" onSubmit={this.sumList}>
            <button type="submit">Enter</button>
        </form>
      </header>
          </div>
        );
       }
    }
    export default ListItems;
