import React, { Component } from 'react';
import './Results.css';

export class Results extends Component {




    render() {

    const { values, handleChange, myStep } = this.props; 
    const totalAfterTax = (parseFloat(values.sum) * parseFloat(values.salesTax)) + parseFloat(values.sum);
    const totalAfterTip = (totalAfterTax * parseFloat(values.tip)) +totalAfterTax;

    console.log("result.tip: "+values.result.tip + " values.tip: "+ values.tip+ " result.name: "+ values.result.name +" sum: " +values.result.sum)
    const results = values.results;
    console.log("results: "+results)
    const listResults = results.map(result =>

   {
       console.log("result.ID: "+result.name)
       return <div className="Results" key={result.key}>
     <p>
        <label>Name:  <b>{result.name} </b></label>
     </p>
     <p>
        <label>total:  <b>{result.sum} </b></label>
     </p>
     <p>
        <label>total after tax:  <b>{result.totalAfterTax} </b></label>
     </p>
     <p>
        <label>total after tip:  <b>{result.totalAfterTip} </b></label>
     </p>
    </div>})
    return <div>
    <h3><center>Results!</center></h3>
    <p>{"Name: "+values.name}</p>
    <p>{"total: "+ values.result.sum}</p>
    <p>{"total After Tax: "+totalAfterTax}</p>
    <p>{"total After tip: "+totalAfterTip}</p>
              {listResults}
      <form id="tip-form" onSubmit={myStep}>
             <button type="submit">Click here to Calculate another person or refresh page for another bill</button>
    </form>

    </div>;

    }

}
export default Results;
