import React, { Component } from 'react';
import ItemLister from './ItemLister'
export class ListItems extends Component {
    constructor(props){
        super(props);
        this.state = {
          entries:[],
          entry:{
            name:'',
            items:[]
          },
          currentItem:{
            text:'',
            key:''
          }
        }
        this.addItem = this.addItem.bind(this);
        this.handleEntry = this.handleEntry.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.setUpdate = this.setUpdate.bind(this);
        this.sumList = this.sumList.bind(this);
      }
      addItem(e){
        e.preventDefault();
        const newItem = this.state.currentItem;

        if(newItem.text !==""){
          const items = [...this.state.entry.items, newItem];
        this.setState({
          entries: items,
          currentItem:{
            text:'',
            key:''
          }
        })
        }
      }
      handleEntry (e){
        this.setState({
            entry: {
                name: e.target.value,
            }

        })
      }



      sumList(e){
        e.preventDefault();
        const list = this.state.items;
        var total = 0;
        for(var i = 0, len = list.lenght; i<len; i++) {
            total += list[i][1];
        }
        const items = [...this.state.sumItems, total]
        this.setState({
            sumItems: items
        })
      }
      deleteItem(key){
        const filteredItems= this.state.items.filter(item =>
          item.key!==key);
        this.setState({
          items: filteredItems
        })

      }
      setUpdate(text,key){
        const items = this.state.items;
        items.map(item=>{      
          if(item.key===key){
            console.log(item.key +"    "+key)
            item.text= text;
          }
        })
        this.setState({
          items: items
        })


      }
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };
    render(){
        console.log("this.props:"+this.props.values.numPeople+" "+this.props.values.salesTax)
        return (
          <div className="App">
            <h3><center>Enter the price of all the items one at a time!</center></h3>
            <header>

        <form id="to-do-form" onSubmit={this.addItem}>
          <input type = "text" placeholder="Enter Name" value= {this.state.entry.name} onChange={this.handleEntry}></input>

          <button type="submit">Add</button>
        </form>



        <form id="submitButton" onSubmit={this.sumList}>
            <button type="submit">Enter</button>
        </form>
      </header>
          </div>
        );
       }
    }
    export default ListItems;
