import React, { Component } from 'react';
import ItemLister from './ItemLister'
export class ListItems extends Component {
    /*constructor(props){
        super(props);
        this.state = {
          items:[],
          sum:0,
          currentItem:{
            text:'',
            key:''
          }
        }
        this.addItem = this.addItem.bind(this);
        this.handleItem = this.handleItem.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.setUpdate = this.setUpdate.bind(this);
        this.sumList = this.sumList.bind(this);
      }*/
      //values = this.props;
      addItem(e){
        e.preventDefault();
        const newItem = this.props.values.currentItem;

        if(newItem.text !==""&& !isNaN(newItem.text)){
          const items = [...this.props.values.items, newItem];
        this.props.setState({
          items: items,
          currentItem:{
            text:'',
            key:''
          }
        })
        }
      }

      handleItem (e) {
        this.props.setState({
            currentItem:{
                text: e.target.value,
                key: Date.now()
             }

        })
      }


      sumList(e){
        e.preventDefault();
        const list = this.props.values.items;
        var total = 0;
        list.map(item=> {
            var value = parseFloat(item.text);
            total += value;

        })
        this.setState({
            sum: total
        })
        this.continue(e);
        console.log("Total: "+total)
      }
      deleteItem(key){
        const filteredItems= this.props.values.items.filter(item =>
          item.key!==key);
        this.props.setState({
          items: filteredItems
        })

      }
      setUpdate(text,key){
        const items = this.state.items;
        items.map(item=>{      
          if(item.key===key){
            console.log(item.key +"    "+key)
            item.text= text;
          }
        })
        this.props.setState({
          items: items
        })


      }
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };
    render(){
        console.log("this.props:"+this.props.values.numPeople+" "+this.props.values.salesTax)
        return (
          <div className="App">
            <h3><center>Enter the price of all the items one at a time for {this.props.values.name}!</center></h3>
            <header>

        <form id="to-do-form" onSubmit={this.addItem}>
          <input type="number" placeholder="Enter task" value= {this.props.values.currentItem.text} onChange={this.handleItem}></input>
          <button type="submit">Add</button>
        </form>
        <p>{this.state.items.text}</p>

        <ItemLister items={this.props.values.items} deleteItem={this.deleteItem} setUpdate={this.setUpdate} sumList={this.sumList}/>
        <form id="submitButton" onSubmit={this.sumList}>
            <button type="submit">Enter</button>
        </form>
      </header>
          </div>
        );
       }
    }
    export default ListItems;
