import React, { Component } from 'react';

export class ListItems extends Component {
    constructor(props){
        super(props);
        this.state = {
          items:[],
          currentItem:{
            text:'',
            key:''
          }
        }
        this.addItem = this.addItem.bind(this);
        this.handleInput = this.handleInput.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.setUpdate = this.setUpdate.bind(this);
      }
      addItem(e){
        e.preventDefault();
        const newItem = this.state.currentItem;
        if(newItem.text !==""){
          const items = [...this.state.items, newItem];
        this.setState({
          items: items,
          currentItem:{
            text:'',
            key:''
          }
        })
        }
      }
      handleInput(e){
        this.setState({
          currentItem:{
            text: e.target.value,
            key: Date.now()
          }
        })
      }
      deleteItem(key){
        const filteredItems= this.state.items.filter(item =>
          item.key!==key);
        this.setState({
          items: filteredItems
        })

      }
      setUpdate(text,key){
        const items = this.state.items;
        items.map(item=>{      
          if(item.key===key){
            console.log(item.key +"    "+key)
            item.text= text;
          }
        })
        this.setState({
          items: items
        })


      }
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };
    render(){
        console.log(this.props)
        return (
          <div className="App">
            <h3><center>Enter the price of all the items one at a time!</center></h3>

          </div>
        );
       }
    }
    export default ListItems;
