import React, { Component } from 'react';
export class GetTip extends Component{
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };

    render() {


        const { values, handleChange } = this.props;

        console.log("values: "+values.salesTax+" "+values.numPeople)
        return (
            <div className="FromUserDetails">
                <h3><center>Enter your desired tip percentage in decimal form</center></h3>
                <form id="initial-form" onSubmit={this.continue}>
                    <input type="number" placeholder="enter tip %" onChange={handleChange('numPeople')} value={values.tip}></input>
                    <button type="submit">Submit</button>
                </form>
            </div>
        );
    }
}
